    internal class RandomEffects
    {
        internal static readonly Random Random = new Random();
 
        internal static void GetRandomEffects()
        {
            ParseTxt();
            RandomizeFile();
            bool exit;
            do
            {
                exit = false;
                Console.Clear();
                GetRandomEffect();
                GetDuration();
                Console.WriteLine("Another? [Y/N]");
                if (Console.ReadKey().Key == ConsoleKey.Y) exit = true;
            } while (exit);
        }

        private static void GetDuration()
        {
            var lines = FileManipulation.ReadTxt(@"Durations");
            var line = lines[Random.Next(0, lines.Length)];
            if (new Regex(@"\d+d\d+").IsMatch(line)) line = DiceRoller.RollDice(line, Random);
            Console.WriteLine(line);
        }

        private static void GetRandomEffect()
        {
            var lines = FileManipulation.ReadTxt(@"KeptEntries");
            var line = lines[Random.Next(0, lines.Length)];
            if (new Regex(@"\d+d\d+").IsMatch(line)) line = DiceRoller.RollDice(line, Random);
            Console.WriteLine(line);
        }

        private static void RandomizeFile()
        {
            var lines = FileManipulation.ReadTxt(@"KeptEntries").ToList();

            for (var i = 0; i < lines.Count * 100; i++)
            {
                var spot = Random.Next(0, lines.Count);
                var temp = lines[spot];
                var spot2 = Random.Next(0, lines.Count);

                while (spot == spot2) spot2 = Random.Next(0, lines.Count);

                lines[spot] = lines[spot2];
                lines[spot2] = temp;
            }

            FileManipulation.WriteStringToTxt(lines, @"KeptEntries");
        }

